import { useSelector, useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import { useEffect } from 'react';
import { getRecipeId } from '../../redux/actions';

const Detail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const recipe = useSelector((state) => state.recipe);

  useEffect(() => {
    dispatch(getRecipeId(id));
  }, [dispatch, id]);

  if (!recipe) {
    return <div>Receta no encontrada</div>;
  }

  return (
    <div>
      <h1>Estamos en la view Detail</h1>
      <h2>{recipe.name && recipe.name}</h2>
      <img src={recipe.image && recipe.image} alt="" />
      <h3>{recipe.summary && recipe.summary}</h3>
      <h3>{recipe.healthScore && recipe.healthScore}</h3>
      <h3>{recipe.steps && recipe.steps}</h3>
      <h3>{recipe.name && recipe.name}</h3>
    </div>
  );
};

export default Detail;


