import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Select from 'react-select';

const Form = () => {
  const dispatch = useDispatch();
  const [name, setName] = useState('');
  const [summary, setSummary] = useState('');
  const [image, setImage] = useState('');
  const [healthScore, setHealthScore] = useState('');
  const [diets, setDiets] = useState([]);
  const [dietOptions, setDietOptions] = useState([]);
  const [steps, setSteps] = useState('');

  useEffect(() => {
    // Obtener las opciones de dietas desde la API
    const fetchDiets = async () => {
      try {
        const response = await axios.get('http://localhost:3001/diets');
        setDietOptions(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchDiets();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Realizar la solicitud POST al backend
      const response = await axios.post('http://localhost:3001/recipes', {
        name,
        summary,
        image,
        healthScore,
        dietss: diets.map((diet) => diet.value), // Obtener los valores seleccionados de los objetos de dieta
        steps,
      });

      console.log(response.data); // Manejar la respuesta del backend según sea necesario

      // Limpiar los campos del formulario
      setName('');
      setSummary('');
      setImage('');
      setHealthScore('');
      setDiets([]);
      setSteps('');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
      </label>
      <br />
      <label>
        Summary:
        <input type="text" value={summary} onChange={(e) => setSummary(e.target.value)} />
      </label>
      <br />
      <label>
        Image:
        <input type="text" value={image} onChange={(e) => setImage(e.target.value)} />
      </label>
      <br />
      <label>
        Health Score:
        <input type="number" value={healthScore} onChange={(e) => setHealthScore(e.target.value)} />
      </label>
      <br />
      <label>
        Diets:
        <Select
          isMulti
          options={dietOptions.map((diet) => ({ value: diet.name, label: diet.name }))}
          value={diets}
          onChange={setDiets}
        />
      </label>
      <br />
      <label>
        Steps:
        <textarea value={steps} onChange={(e) => setSteps(e.target.value)}></textarea>
      </label>
      <br />
      <button type="submit">Submit</button>
    </form>
  );
};

export default Form;



