import { useState, useEffect } from 'react';
import Select from 'react-select';
import { useDispatch, useSelector } from 'react-redux';
import { addRecipe, getDiets } from '../../redux/actions';
import styles from './Form.module.css';

const Form = () => {
  const dispatch = useDispatch();
  const dietOptions = useSelector((state) => state.dietOptions);

  const [form, setForm] = useState({
    name: '',
    summary: '',
    healthScore: '',
    stepByStep: '',
    image: '',
    diets: [],
  });

  const [errors, setErrors] = useState({});

  useEffect(() => {
    dispatch(getDiets());
  }, [dispatch]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
    setErrors({ ...errors, [name]: null }); // Resetear el mensaje de error al cambiar el valor del campo
  };

  const handleSelectChange = (selectedOptions) => {
    const selectedDiets = selectedOptions.map((option) => option.value);
    setForm({ ...form, diets: selectedDiets });
    setErrors({ ...errors, diets: null }); // Resetear el mensaje de error al cambiar las dietas seleccionadas
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const validationErrors = {};

    if (!/^[A-Za-z\s]+$/.test(form.name)) {
      validationErrors.name = 'El nombre solo debe contener letras y espacios, sin números ni símbolos';
    }

    if (form.summary.trim().split(' ').length < 2) {
      validationErrors.summary = 'El resumen debe tener al menos dos palabras';
    }

    const healthScore = parseInt(form.healthScore);
    if (isNaN(healthScore) || healthScore < 1 || healthScore > 100) {
      validationErrors.healthScore = 'El healthScore debe ser un número entre 1 y 100';
    }

    if (form.stepByStep.trim().split(' ').length < 2) {
      validationErrors.stepByStep = 'Los pasos deben tener al menos dos palabras';
    }

    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    try {
      dispatch(addRecipe(form));
      setForm({
        name: '',
        summary: '',
        healthScore: '',
        stepByStep: '',
        image: '',
        diets: [],
      });
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className={styles['form-wrapper']}>
      <form className={styles['form-container']} onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" value={form.name} onChange={handleChange} name="name" />
          {errors.name && <span className={styles['error-message']}>{errors.name}</span>}
        </label>
        <br />
        <label>
          Summary:
          <input type="text" value={form.summary} onChange={handleChange} name="summary" />
          {errors.summary && <span className={styles['error-message']}>{errors.summary}</span>}
        </label>
        <br />
        <label>
          URL Image:
          <input type="text" value={form.image} onChange={handleChange} name="image" />
        </label>
        <br />
        <label>
          Health Score:
          <input type="number" value={form.healthScore} onChange={handleChange} name="healthScore" />
          {errors.healthScore && <span className={styles['error-message']}>{errors.healthScore}</span>}
        </label>
        <br />
        <label>
          Diets:
          <div className={styles['select-container']}>
            <Select
              isMulti
              options={dietOptions.map((diet) => ({ value: diet.name, label: diet.name }))}
              onChange={handleSelectChange}
            />
          </div>
          {errors.diets && <span className={styles['error-message']}>{errors.diets}</span>}
        </label>
        <br />
        <label>
          Steps:
          <textarea value={form.stepByStep} onChange={handleChange} name="stepByStep"></textarea>
          {errors.stepByStep && <span className={styles['error-message']}>{errors.stepByStep}</span>}
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default Form;
