import React, { useState } from 'react';
import axios from 'axios';

const RecipeForm = () => {
  const [name, setName] = useState('');
  const [summary, setSummary] = useState('');
  const [image, setImage] = useState('');
  const [healthScore, setHealthScore] = useState('');
  const [diets, setDiets] = useState([]);
  const [steps, setSteps] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Realizar la solicitud POST al backend
      const response = await axios.post('/recipes', {
        name,
        summary,
        image,
        healthScore,
        diets,
        steps,
      });

      console.log(response.data); // Manejar la respuesta del backend según sea necesario

      // Limpiar los campos del formulario
      setName('');
      setSummary('');
      setImage('');
      setHealthScore('');
      setDiets([]);
      setSteps('');
    } catch (error) {
      console.error(error);
    }
  };

  const handleDietChange = (e) => {
    const selectedDiets = Array.from(e.target.selectedOptions, (option) => option.value);
    setDiets(selectedDiets);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
      </label>
      <br />
      <label>
        Summary:
        <input type="text" value={summary} onChange={(e) => setSummary(e.target.value)} />
      </label>
      <br />
      <label>
        Image:
        <input type="text" value={image} onChange={(e) => setImage(e.target.value)} />
      </label>
      <br />
      <label>
        Health Score:
        <input type="number" value={healthScore} onChange={(e) => setHealthScore(e.target.value)} />
      </label>
      <br />
      <label>
        Diets:
        <select multiple value={diets} onChange={handleDietChange}>
          <option value="diet1">Diet 1</option>
          <option value="diet2">Diet 2</option>
          <option value="diet3">Diet 3</option>
          {/* Agrega más opciones de dieta según sea necesario */}
        </select>
      </label>
      <br />
      <label>
        Steps:
        <textarea value={steps} onChange={(e) => setSteps(e.target.value)}></textarea>
      </label>
      <br />
      <button type="submit">Submit</button>
    </form>
  );
};

export default RecipeForm;


