import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getRecipes, setSelectedDietTypes } from '../../redux/actions';
import Card from '../Card/Card';
import styles from './Cards.module.css';

const Cards = () => {
  const dispatch = useDispatch();
  const recipes = useSelector((state) => state.recipes);
  const currentPage = useSelector((state) => state.currentPage);
  const selectedTypes = useSelector((state) => state.selectedTypes);
  const recipesPerPage = 10; // Número de recetas por página

  useEffect(() => {
    dispatch(getRecipes());
  }, [dispatch]);

  const handleTypeChange = (event) => {
    const selectedOptions = Array.from(event.target.selectedOptions, (option) => option.value);
    setSelectedTypes(selectedOptions);
  };

  const indexOfLastRecipe = currentPage * recipesPerPage;
  const indexOfFirstRecipe = indexOfLastRecipe - recipesPerPage;
  const currentRecipes = recipes.slice(indexOfFirstRecipe, indexOfLastRecipe);

  return (
    <div className={styles.container}>
      <div className={styles.filter}>
        <label htmlFor="recipeTypes">Tipos de Receta:</label>
        <select
          id="recipeTypes"
          multiple
          value={selectedTypes}
          onChange={handleTypeChange}
        >
          <option value="Desayuno">Desayuno</option>
          <option value="Almuerzo">Almuerzo</option>
          <option value="Cena">Cena</option>
          {/* Agrega aquí más opciones de tipos de receta */}
        </select>
        <button onClick={() => setSelectedTypes([])}>Limpiar</button>
      </div>

      {currentRecipes
        .filter((recipe) => selectedTypes.length === 0 || selectedTypes.includes(recipe.type))
        .map((recipe) => {
          let diets = [];
          if (Array.isArray(recipe.diets)) {
            diets = recipe.diets;
          }

          return (
            <Card
              id={recipe.id}
              key={recipe.id}
              name={recipe.name}
              image={recipe.image}
              diet={diets}
            />
          );
        })}
    </div>
  );
};

export default Cards;


