import {GET_RECIPES} from "./actions";

const initialState = {
    recipes: [],
}


//recibe estado a modificar, recibe action a modificar
const rootReducer = (state = initialState, action) =>{
    switch(action.type){ //evalua action types
        case GET_RECIPES:
        return {...state, //devuelve un estado nuevo trayendo la info vieja y modificando la propiedad con la accio
            recipes: action.payload} //redux
        default:
            return {...state};
    }
}

export default rootReducer;
