import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getRecipes, setSelectedDietTypes } from '../../redux/actions'; // Asegúrate de tener la acción 'setSelectedDietTypes' definida en tus acciones
import Card from '../Card/Card';
import styles from './Cards.module.css';

const Cards = () => {
  const dispatch = useDispatch();
  const recipes = useSelector((state) => state.recipes);
  const currentPage = useSelector((state) => state.currentPage);
  const recipesPerPage = 10; // Número de recetas por página
  const [selectedDietTypes, setSelectedDietTypes] = useState([]);

  useEffect(() => {
    dispatch(getRecipes());
  }, [dispatch]);

  // Obtener todos los tipos de dieta disponibles
  const dietTypes = [...new Set(recipes.map((recipe) => recipe.diet))];

  // Lógica para obtener las recetas de la página actual
  const indexOfLastRecipe = currentPage * recipesPerPage;
  const indexOfFirstRecipe = indexOfLastRecipe - recipesPerPage;
  const currentRecipes = recipes.slice(indexOfFirstRecipe, indexOfLastRecipe);

  // Manejar cambios en el desplegable de dietas
  const handleDietTypeChange = (event) => {
    const { value, checked } = event.target;
    if (checked) {
      setSelectedDietTypes((prevSelectedDietTypes) => [...prevSelectedDietTypes, value]);
    } else {
      setSelectedDietTypes((prevSelectedDietTypes) => prevSelectedDietTypes.filter((type) => type !== value));
    }
  };

  // Filtrar las recetas según las dietas seleccionadas
  const filteredRecipes = selectedDietTypes.length > 0
    ? recipes.filter((recipe) => selectedDietTypes.includes(recipe.diet))
    : recipes;

  return (
    <div className={styles.container}>
      {/* Desplegable para filtrar por dietas */}
      <div>
        <label htmlFor="dietFilter">Filtrar por dieta:</label>
        <select
          id="dietFilter"
          multiple
          value={selectedDietTypes}
          onChange={handleDietTypeChange}
        >
          {/* Opciones del desplegable */}
          {dietTypes.map((dietType) => (
            <option key={dietType} value={dietType}>{dietType}</option>
          ))}
        </select>
      </div>

      {/* Mostrar las recetas filtradas */}
      {currentRecipes.map((recipe) => (
        <Card
          id={recipe.id}
          key={recipe.id}
          name={recipe.name}
          image={recipe.image}
          diet={recipe.diet}
        />
      ))}
    </div>
  );
};

export default Cards;


