import { GET_RECIPES, ADD_RECIPE, GET_DIETS } from "./actions";

const initialState = {
  recipes: [],
  dietOptions: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_RECIPES:
      return {
        ...state,
        recipes: action.payload,
      };
    case ADD_RECIPE:
      return {
        ...state,
        recipes: [...state.recipes, action.payload],
      };
      case GET_DIETS: // Agregar el caso para la acción GET_DIETS
      return {
        ...state,
        dietOptions: action.payload,
      };  
    default:
      return state;
  }
};

export default rootReducer;
