import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getRecipes } from '../../redux/actions';
import Card from '../Card/Card';
import styles from './Cards.module.css';

const Cards = () => {
  const dispatch = useDispatch();
  const recipes = useSelector((state) => state.recipes);
  const currentPage = useSelector((state) => state.currentPage);
  const recipesPerPage = 10; // Número de recetas por página

  useEffect(() => {
    dispatch(getRecipes());
  }, [dispatch]);

  // Lógica para ordenar las recetas por ID ascendente
  const sortedRecipes = recipes.sort((a, b) => a.id.localeCompare(b.id));

  // Lógica para obtener las recetas de la página actual
  const indexOfLastRecipe = currentPage * recipesPerPage;
  const indexOfFirstRecipe = indexOfLastRecipe - recipesPerPage;
  const currentRecipes = sortedRecipes.slice(indexOfFirstRecipe, indexOfLastRecipe);

  console.log(currentRecipes); // Verificar si se obtienen las recetas correctamente

  return (
    <div className={styles.container}>
      {currentRecipes.length > 0 ? (
        currentRecipes.map((recipe) => (
          <Card
            id={recipe.id}
            key={recipe.id}
            name={recipe.name}
            image={recipe.image}
            diet={recipe.diets}
          />
        ))
      ) : (
        <p>No se encontraron recetas.</p>
      )}
    </div>
  );
};

export default Cards;


