import  { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getRecipes } from '../../redux/actions';
import Cards from '../Cards/Cards';
import Pagination from '../Pagination/Pagination';
import styles from './HomePage.module.css';

const HomePage = () => {
  const dispatch = useDispatch();
  const [alphabeticalOrder, setAlphabeticalOrder] = useState('');
  const [healthScoreOrder, setHealthScoreOrder] = useState('');

  useEffect(() => {
    dispatch(getRecipes());
  }, [dispatch]);

  const handleAlphabeticalOrderChange = (event) => {
    setAlphabeticalOrder(event.target.value);
  };

  const handleHealthScoreOrderChange = (event) => {
    setHealthScoreOrder(event.target.value);
  };

  return (
    <div className={styles.homeContainer}>
      <h1>HOME PAGE:</h1>
      <h2>Aqui deben ir botones de filtro, de ordenamiento y de crear receta</h2>

      <div className={styles.filterDropdowns}>
        <div className={styles.filterDropdown}>
          <select
            className={styles.selectBox}
            value={alphabeticalOrder}
            onChange={handleAlphabeticalOrderChange}
          >
            <option value="">Orden Alfab√©tico</option>
            <option value="asc">A-Z</option>
            <option value="desc">Z-A</option>
          </select>
        </div>

        <div className={styles.sortDropdown}>
          <select
            className={styles.selectBox}
            value={healthScoreOrder}
            onChange={handleHealthScoreOrderChange}
          >
            <option value="">Orden Health Score</option>
            <option value="desc">Very Healthy</option>
            <option value="asc">Poorly Healthy</option>
          </select>
        </div>
      </div>

      <Pagination />
      <Cards alphabeticalOrder={alphabeticalOrder} healthScoreOrder={healthScoreOrder} />
      <Pagination />
    </div>
  );
};

export default HomePage;
