import axios from 'axios';


export const GET_RECIPES = "GET_RECIPES";

//action creator. Retorna una funcion que hace la peticion y despacha la action
export const getRecipes = () => {
    return async function(dispatch){
        const apiRecipes = await axios.get(`https://api.spoonacular.com/recipes/complexSearch?apiKey=${API_KEY}&number=100&addRecipeInformation=true&number=100`)
        
        const recipes = apiRecipes.data;
        dispatch({type: GET_RECIPES, payload: recipes})
    }
}
