import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Select from 'react-select';
import { useDispatch } from 'react-redux';
import { addRecipe } from '../../redux/actions';

const Form = () => {
  const dispatch = useDispatch();
  const [form, setForm] = useState({
    name: '',
    summary: '',
    image: '',
    healthScore: '',
    diets: [],
    steps: '',
  });
  const [dietOptions, setDietOptions] = useState([]);

  useEffect(() => {
    // Obtener las opciones de dietas desde la API
    const fetchDiets = async () => {
      try {
        const response = await axios.get('http://localhost:3001/diets');
        setDietOptions(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchDiets();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleSelectChange = (selectedOptions) => {
    setForm({ ...form, diets: selectedOptions });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Realizar la solicitud POST al backend
      const response = await axios.post('http://localhost:3001/recipes', form);

      console.log(response.data); // Manejar la respuesta del backend según sea necesario

      // Limpiar los campos del formulario
      setForm({
        name: '',
        summary: '',
        image: '',
        healthScore: '',
        diets: [],
        steps: '',
      });

      dispatch(addRecipe(response.data));
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input type="text" value={form.name} onChange={handleChange} name="name" />
      </label>
      <br />
      <label>
        Summary:
        <input type="text" value={form.summary} onChange={handleChange} name="summary" />
      </label>
      <br />
      <label>
        Image:
        <input type="text" value={form.image} onChange={handleChange} name="image" />
      </label>
      <br />
      <label>
        Health Score:
        <input type="number" value={form.healthScore} onChange={handleChange} name="healthScore" />
      </label>
      <br />
      <label>
        Diets:
        <Select
          isMulti
          options={dietOptions.map((diet) => ({ value: diet.name, label: diet.name }))}
          value={form.diets}
          onChange={handleSelectChange}
        />
      </label>
      <br />
      <label>
        Steps:
        <textarea value={form.steps} onChange={handleChange} name="steps"></textarea>
      </label>
      <br />
      <button type="submit">Submit</button>
    </form>
  );
};

export default Form;



