import { useState, useEffect } from 'react';
import Select from 'react-select';
import { useDispatch, useSelector } from 'react-redux';
import { addRecipe, getDiets } from '../../redux/actions';
import styles from './Form.module.css';

const Form = () => {
  const dispatch = useDispatch();
  const dietOptions = useSelector((state) => state.dietOptions);

  const [form, setForm] = useState({
    name: { value: '', error: '' },
    summary: { value: '', error: '' },
    healthScore: { value: '', error: '' },
    stepByStep: { value: '', error: '' },
    image: { value: '', error: '' },
    diets: { value: [], error: '' },
  });

  useEffect(() => {
    dispatch(getDiets());
  }, [dispatch]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: { ...form[name], value } });
    validateField(name, value);
  };

  const handleSelectChange = (selectedOptions) => {
    const selectedDiets = selectedOptions.map((option) => option.value);
    setForm({ ...form, diets: { ...form.diets, value: selectedDiets } });
    validateField('diets', selectedDiets);
  };

  const validateField = (name, value) => {
    let error = '';

    if (name === 'name') {
      if (!/^[A-Za-z\s]+$/.test(value)) {
        error = 'El nombre solo debe contener letras y espacios, sin números ni símbolos';
      }
    } else if (name === 'summary') {
      if (value.trim().split(' ').length < 2) {
        error = 'El resumen debe tener al menos dos palabras';
      }
    } else if (name === 'healthScore') {
      const healthScore = parseInt(value);
      if (isNaN(healthScore) || healthScore < 1 || healthScore > 100) {
        error = 'El healthScore debe ser un número entre 1 y 100';
      }
    } else if (name === 'stepByStep') {
      if (value.trim().split(' ').length < 2) {
        error = 'Los pasos deben tener al menos dos palabras';
      }
    } else if (name === 'diets') {
      if (value.length === 0) {
        error = 'Debes seleccionar al menos una dieta';
      }
    }

    setForm({ ...form, [name]: { ...form[name], error } });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const validationErrors = {};

    Object.keys(form).forEach((name) => {
      const value = form[name].value;
      validateField(name, value);
      if (form[name].error) {
        validationErrors[name] = form[name].error;
      }
    });

    if (Object.keys(validationErrors).length > 0) {
      return;
    }

    try {
      dispatch(addRecipe(form));
      resetForm();
    } catch (error) {
      console.error(error);
    }
  };

  const resetForm = () => {
    const initialForm = {
      name: { value: '', error: '' },
      summary: { value: '', error: '' },
      healthScore: { value: '', error: '' },
      stepByStep: { value: '', error: '' },
      image: { value: '', error: '' },
      diets: { value: [], error: '' },
    };

    setForm(initialForm);
  };

  return (
    <div className={styles['form-wrapper']}>
      <form className={styles['form-container']} onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" value={form.name.value} onChange={handleChange} name="name" />
          {form.name.error && <span className={styles['error-message']}>{form.name.error}</span>}
        </label>
        <br />
        <label>
          Summary:
          <input type="text" value={form.summary.value} onChange={handleChange} name="summary" />
          {form.summary.error && <span className={styles['error-message']}>{form.summary.error}</span>}
        </label>
        <br />
        <label>
          URL Image:
          <input type="text" value={form.image.value} onChange={handleChange} name="image" />
          {form.image.error && <span className={styles['error-message']}>{form.image.error}</span>}
        </label>
        <br />
        <label>
          Health Score:
          <input type="number" value={form.healthScore.value} onChange={handleChange} name="healthScore" />
          {form.healthScore.error && <span className={styles['error-message']}>{form.healthScore.error}</span>}
        </label>
        <br />
        <label>
          Diets:
          <div className={styles['select-container']}>
            <Select
              isMulti
              options={dietOptions.map((diet) => ({ value: diet.name, label: diet.name }))}
              onChange={handleSelectChange}
              value={form.diets.value}
            />
          </div>
          {form.diets.error && <span className={styles['error-message']}>{form.diets.error}</span>}
        </label>
        <br />
        <label>
          Steps:
          <textarea value={form.stepByStep.value} onChange={handleChange} name="stepByStep"></textarea>
          {form.stepByStep.error && <span className={styles['error-message']}>{form.stepByStep.error}</span>}
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default Form;
