import { useSelector, useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import { useEffect } from 'react';
import { getRecipeId, cleanDetail } from '../../redux/actions';
import styles from './Detail.module.css';

const Detail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const recipe = useSelector((state) => state.recipe);

  useEffect(() => {
    dispatch(getRecipeId(id));
    return () => {
      dispatch(cleanDetail()); // Limpia los datos al desmontar el componente
    };
  }, [dispatch, id]);

  if (!recipe) {
    return <div className={styles['error-message']}>Receta no encontrada</div>;
  }

  const renderInstructions = () => {
    if (recipe.analyzedInstructions && recipe.analyzedInstructions.length > 0) {
      return recipe.analyzedInstructions[0].steps.map((step, index) => (
        <li key={index}>{step.step}</li>
      ));
    } else {
      // Si no hay instrucciones disponibles, muestra un mensaje de error o una indicación apropiada
      return <li>No hay instrucciones disponibles para esta receta.</li>;
    }
  };

  return (
    <div className={styles.container}>
      <h1>{recipe.title}</h1>
      <ul>
        <li>ID: {recipe.id}</li>
        <li>Nombre: {recipe.title}</li>
        <li className={styles.summary}>Resumen de plato: {recipe.summary}</li>
        <li className={styles['health-score']}>Health Score: {recipe.healthScore}</li>
        {recipe.analyzedInstructions && recipe.analyzedInstructions.length > 0 && (
          <li className={styles.steps}>
            <strong>Pasos:</strong>
            <ul>{renderInstructions()}</ul>
          </li>
        )}
        <li>
          <img src={recipe.image} alt={recipe.title} />
        </li>
        <ul className={styles['diet-list']}>
          Diet:
          {recipe.diets.map((diet, index) => (
            <li key={index}>{diet}</li>
          ))}
        </ul>
      </ul>
    </div>
  );
};

export default Detail;

