import {useState} from "react";
import axios from "axios";
import { v4 as uuidv4 } from "uuid";




export const Form = () => {
  
  const [form, setForm] = useState ({
    id: uuidv4(),
    name:"",
    summary:"",
    image:"",
    healthScore:"",
    stepByStep:"",
    dietss:"",
  })

// const [errors, serErrors] = useState ({
//     recipeName:"",
//     summary:"",
//     healthScore:"",
//     steps:"",
//     image:"",
//     diet:"",
// })
  
// const validate = (form) => {
  
// }

const changeHandler = (event) =>{
  //me trae informacion de donde estoy disparando el evento. Que input disparo el evento.
  const property = event.target.name
  const value = event.target.value

  setForm({...form, [property] : value})
}

const submitHandler = (event) => {
  event.preventDefault();
  axios
    .post("http://localhost:3001/recipes", form)
    .then((res) => {
      alert("Receta creada exitosamente");
      // Restablecer el formulario después de la creación exitosa
      setForm({
        id: uuidv4(),
        name: "",
        summary: "",
        image: "",
        healthScore: "",
        stepByStep: "",
        dietss: "",
      });
    })
    .catch((error) => alert(error));
}

  return (
    <form onSubmit={submitHandler}>
      <div>
        <label >Recipe Name</label>
        <input type="text" value={form.name} onChange={changeHandler} name="name"/>
      </div>
      <div>
        <label >Resumen Plato</label>
        <input type="text" value={form.summary} onChange={changeHandler} name="summary" />
      </div>
      <div>
        <label >health score--desplegable o input</label>
        <input type="text" value={form.healthScore} onChange={changeHandler} name="healthScore" />
      </div>
      <div>
        <label >Steps</label>
        <input type="text" value={form.stepByStep} onChange={changeHandler} name="stepByStep" />
      </div>
      <div>
        <label >Img--que permita cargar imagenes</label>
        <input type="text" value={form.image} onChange={changeHandler} name="image" />
      </div>
      <div>
        <label >Diet Types --selector desplegab</label>
        <input type="text" value={form.dietss} onChange={changeHandler} name="dietss" />
      </div>
      <button>Create Recipe</button>
    </form>
  )
}

export default  Form
