import axios from "axios"



export const GET_RECIPES = "GET_RECIPES";

//trae todas las recetas
export const getRecipes = () => {
    return async function (dispatch){
        const apiData = await axios.get(`https://api.spoonacular.com/recipes/complexSearch?apiKey=e3d0550de4e44268a7e99d7b08980b7c&number=100&addRecipeInformation=true&number=100`)
        const recipes = apiData.data;
        dispatch({type: GET_RECIPES, payload: recipes})
    }
}

//trae el detalles las recetas
export const getRecipe = () => {
    return async function (dispatch){
        const apiData = await axios.get(`https://api.spoonacular.com/recipes/${idRecipe}/information?apiKey=${API_KEY}`)
        const user = apiData.data;
        dispatch({type: GET_USER, payload: user})
    }
}
