import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getRecipes, sortRecipes, filterByDiet, setCurrentPage } from '../../redux/actions';
import Cards from '../Cards/Cards';
import styles from './HomePage.module.css';
import Pagination from '../Pagination/Pagination';

const HomePage = () => {
  const dispatch = useDispatch();

  const [sortOption, setSortOption] = useState('alphabetical');
  const [sortDirection, setSortDirection] = useState('asc');
  const selectedDietType = useSelector((state) => state.selectedDietType);
  const dietOptions = useSelector((state) => state.dietOptions);

  useEffect(() => {
    dispatch(getRecipes());
  }, [dispatch]);

  useEffect(() => {
    dispatch(sortRecipes(sortOption, sortDirection));
  }, [dispatch, sortOption, sortDirection]);

  const handleSortOption = (option) => {
    setSortOption(option);
  };

  const handleSortDirection = (direction) => {
    setSortDirection(direction);
  };

  const handleFilterDiets = (e) => {
    const selectedDiet = e.target.value;
    dispatch(filterByDiet(selectedDiet));
    dispatch(setCurrentPage(1));
  };

  const filteredRecipes = useSelector((state) => {
    const selectedDiet = state.selectedDietType;
    const allRecipes = state.recipes;

    if (selectedDiet === 'all') {
      return allRecipes;
    }

    return allRecipes.filter((recipe) => {
      return recipe.diets.some((diet) => diet.name === selectedDiet);
    });
  });

  return (
    <div className={styles.homeContainer}>
      <div className={styles.filterDropdowns}>
        <div className={styles.filterDropdown}>
          <select value={sortOption} onChange={(e) => handleSortOption(e.target.value)}>
            <option value="alphabetical">Alphabetical</option>
            <option value="healthScore">Health Score</option>
          </select>
        </div>

        {sortOption === 'alphabetical' && (
          <div className={styles.sortDropdown}>
            <select value={sortDirection} onChange={(e) => handleSortDirection(e.target.value)}>
              <option value="asc">A-Z</option>
              <option value="desc">Z-A</option>
            </select>
          </div>
        )}

        {sortOption === 'healthScore' && (
          <div className={styles.sortDropdown}>
            <select value={sortDirection} onChange={(e) => handleSortDirection(e.target.value)}>
              <option value="desc">Very Healthy</option>
              <option value="asc">Poorly Healthy</option>
            </select>
          </div>
        )}

        <select onChange={handleFilterDiets} value={selectedDietType || 'all'}>
          <option value="default" disabled>
            Select...
          </option>
          <option value="all">All</option>
          {dietOptions.map((el) => (
            <option value={el.name} key={el.id}>
              {el.name}
            </option>
          ))}
        </select>
      </div>

      <Pagination />
      <Cards recipes={filteredRecipes} />
      <Pagination />
    </div>
  );
};

export default HomePage;
