import { GET_RECIPES } from "./actions";

const initialState = {
    recipes:  [],
};
  
const rootReducer = (state = initialState, action) =>{
    switch(action.type) {
      case GET_RECIPES:
          return {...state, recipes: action.payload.results}  //redux no modifica el estado, pisa el estado. El estado esunobeto nuevo y modifica la propiedad a traves de 
        default:
            return {...state};
    }
}

export default rootReducer;
