import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getRecipes, sortRecipes, filterByDiet, getDiets, filterBySource } from '../../redux/actions';
import Cards from '../Cards/Cards';
import styles from './HomePage.module.css';
import Pagination from '../Pagination/Pagination';

const HomePage = () => {
  const dispatch = useDispatch();

  const [sortOption, setSortOption] = useState('alphabetical');
  const [sortDirection, setSortDirection] = useState('asc');
  const dietOptions = useSelector((state) => state.dietOptions);
  const selectedDietType = useSelector((state) => state.selectedDietType);
  const recipes = useSelector((state) => state.recipes);
  const [sourceFilter, setSourceFilter] = useState('all');

  const [filteredRecipes, setFilteredRecipes] = useState([]);

  useEffect(() => {
    dispatch(getRecipes());
    dispatch(getDiets());
  }, [dispatch]);

  useEffect(() => {
    dispatch(sortRecipes(sortOption, sortDirection));
  }, [dispatch, sortOption, sortDirection]);

  useEffect(() => {
    if (selectedDietType) {
      const filteredRecipes = recipes.filter((recipe) =>
        recipe.diets.find((diet) => diet.name === selectedDietType)
      );
      setFilteredRecipes(filteredRecipes);
    } else {
      setFilteredRecipes(recipes);
    }
  }, [recipes, selectedDietType]);

  const handleSortOption = (option) => {
    setSortOption(option);
  };

  const handleSortDirection = (direction) => {
    setSortDirection(direction);
  };

  const handleFilterDiets = (e) => {
    e.preventDefault();
    dispatch(filterByDiet(e.target.value));
  };

  const handleFilterSource = (e) => {
    e.preventDefault();
    const source = e.target.value;
    setSourceFilter(source);

    const filteredRecipes = source === 'all'
      ? recipes
      : recipes.filter((recipe) => recipe.source === source);

    dispatch(filterBySource(source));
    setFilteredRecipes(filteredRecipes);
  };

  return (
    <div className={styles.homeContainer}>
      <div className={styles.filterDropdowns}>
        <div className={styles.filterDropdown}>
          <select value={sortOption} onChange={(e) => handleSortOption(e.target.value)}>
            <option value="alphabetical">Alphabetical</option>
            <option value="healthScore">Health Score</option>
          </select>
        </div>

        {sortOption === 'alphabetical' && (
          <div className={styles.sortDropdown}>
            <select value={sortDirection} onChange={(e) => handleSortDirection(e.target.value)}>
              <option value="asc">A-Z</option>
              <option value="desc">Z-A</option>
            </select>
          </div>
        )}

        {sortOption === 'healthScore' && (
          <div className={styles.sortDropdown}>
            <select value={sortDirection} onChange={(e) => handleSortDirection(e.target.value)}>
              <option value="desc">Very Healthy</option>
              <option value="asc">Poorly Healthy</option>
            </select>
          </div>
        )}
      </div>

      <div className={styles.dietFilter}>
        <select onChange={handleFilterDiets} defaultValue="">
          <option value="">Diet Filter</option>
          {dietOptions.map((diet) => (
            <option key={diet} value={diet}>
              {diet}
            </option>
          ))}
        </select>
      </div>

      <div className={styles.sourceFilter}>
        <select onChange={handleFilterSource} defaultValue={sourceFilter}>
          <option value="all">Source Filter</option>
          <option value="option1">Option 1</option>
          <option value="option2">Option 2</option>
        </select>
      </div>

      <Cards recipes={filteredRecipes} />

      <Pagination />
    </div>
  );
};

export default HomePage;
