import { useSelector, useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import { useEffect } from 'react';
import { getRecipeId } from '../../redux/actions';

const Detail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const recipe = useSelector((state) => state.recipe);

  useEffect(() => {
    dispatch(getRecipeId(id));
    
  }, [dispatch, id]);

  if (!recipe) {
    return <div>Receta no encontrada</div>;
  }
  console.log(recipe);
  return (
    <div>
      <h1>{recipe.name}</h1>
      <ul>
        <li>ID: {recipe.id}</li>
        <li>Nombre: {recipe.name }</li>
        <li>Resumen de plato: {recipe.summary}</li>
        <li>Health Score: {recipe.healthScore}</li>
        {recipe.stepByStep?(<li>Steps: {recipe.stepByStep}</li>):(<li>Steps: {recipe.steps}</li>)}
        
        <li>Img: </li> <img src="" alt="" />
        <li>Dietss: {recipe.diets}</li>
      </ul>
    </div>
  );
};

export default Detail;


