import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getRecipes, setSelectedDietType } from '../../redux/actions';
import Card from '../Card/Card';
import styles from './HomePage.module.css';
import Pagination from '../Pagination/Pagination';

const HomePage = () => {
  const dispatch = useDispatch();
  const recipes = useSelector((state) => state.recipes);
  const [selectedDietTypes, setSelectedDietTypes] = useState([]);
  const [selectedOrigin, setSelectedOrigin] = useState('');

  useEffect(() => {
    dispatch(getRecipes());
  }, [dispatch]);

  const handleDietTypeChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
    setSelectedDietTypes(selectedOptions);
    dispatch(setSelectedDietType(selectedOptions));
  };

  const handleOriginChange = (e) => {
    setSelectedOrigin(e.target.value);
  };

  const filteredRecipes = recipes.filter((recipe) => {
    if (selectedDietTypes.length > 0 && !selectedDietTypes.includes(recipe.diet)) {
      return false;
    }
    if (selectedOrigin && selectedOrigin !== recipe.origin) {
      return false;
    }
    return true;
  });

  return (
    <div className={styles.homeContainer}>
      <h1>HOME PAGE:</h1>
      <h2>Aqui deben ir botones de filtro, de ordenamiento y de crear receta</h2>

      <div>
        <label htmlFor="dietTypes">Tipos de Dieta:</label>
        <select id="dietTypes" multiple value={selectedDietTypes} onChange={handleDietTypeChange}>
          {/* Opciones de tipos de dieta */}
        </select>
      </div>

      <div>
        <label htmlFor="origin">Origen de Recetas:</label>
        <select id="origin" value={selectedOrigin} onChange={handleOriginChange}>
          {/* Opciones de origen de recetas */}
        </select>
      </div>

      <Pagination />
      <Cards recipes={filteredRecipes} />
      <Pagination />
    </div>
  );
};

export default HomePage;
